import React, { useEffect, useState } from "react";
import { ethers } from "ethers";
import { CONTRACT_ADDRESS, transformCharacterData } from "../../constants";
import myEpicGame from "../../utils/MyEpicGame.json";
import "./Arena.css";
import LoadingIndicator from "../LoadingIndicator";

// „Éï„É≠„É≥„Éà„Ç®„É≥„Éâ„Å´NFT„Ç≠„É£„É©„ÇØ„Çø„Éº„ÇíË°®Á§∫„Åô„Çã„Åü„ÇÅ„ÄÅcharacterNFT„ÅÆ„É°„Çø„Éá„Éº„Çø„ÇíÊ∏°„Åó„Åæ„Åô„ÄÇ
const Arena = ({ characterNFT, setCharacterNFT }) => {
  // „Ç≥„É≥„Éà„É©„ÇØ„Éà„ÅÆ„Éá„Éº„Çø„Çí‰øùÊúâ„Åô„ÇãÁä∂ÊÖãÂ§âÊï∞„ÇíÂàùÊúüÂåñ„Åó„Åæ„Åô„ÄÇ
  const [gameContract, setGameContract] = useState(null);
  const [boss, setBoss] = useState(null);
  const [attackState, setAttackState] = useState("");
  const [showToast, setShowToast] = useState(false);

  const runAttackAction = async () => {
    try {
      if (gameContract) {
        setAttackState("attacking");
        console.log("Attacking boss...");

        const attackTxn = await gameContract.attackBoss();

        await attackTxn.wait();
        console.log("attackTxn: ", attackTxn);

        setAttackState("hit");
      }
    } catch (error) {
      console.error("Error attacking boss:", error);
      setAttackState("");
    }
  };

  // „Éö„Éº„Ç∏„Åå„É≠„Éº„Éâ„Åï„Çå„Çã„Å®‰∏ãË®ò„ÅåÂÆüË°å„Åï„Çå„Åæ„Åô„ÄÇ
  useEffect(() => {
    const { ethereum } = window;
    if (ethereum) {
      const provider = new ethers.providers.Web3Provider(ethereum);
      const signer = provider.getSigner();
      const gameContract = new ethers.Contract(
        CONTRACT_ADDRESS,
        myEpicGame.abi,
        signer
      );
      setGameContract(gameContract);
    } else {
      console.log("Ethereum object not found");
    }
  }, []);

  useEffect(() => {
    const fetchBoss = async () => {
      const bossTxn = await gameContract.getBigBoss();
      console.log("Boss:", bossTxn);
      setBoss(transformCharacterData(bossTxn));
    };

    const onAttackComplete = (newBossHp, newPlayerHp) => {
      const bossHp = newBossHp.toNumber();

      const playerHp = newPlayerHp.toNumber();
      console.log(`AttackComplete: Boss Hp: ${bossHp} Player Hp: ${playerHp}`);

      setBoss((prevState) => {
        return { ...prevState, hp: bossHp };
      });
      setCharacterNFT((prevState) => {
        return { ...prevState, hp: playerHp };
      });
    }

    if (gameContract) {
      fetchBoss();
      gameContract.on("AttackComplete", onAttackComplete);
    }

    return () => {
      if (gameContract) {
        gameContract.off("AttackComplete", onAttackComplete);
      }
    };
  }, [gameContract]);


  return (
    <div className="arena-container">
      {/* ÊîªÊíÉ„ÉÄ„É°„Éº„Ç∏„ÅÆÈÄöÁü•„ÇíËøΩÂä†„Åó„Åæ„Åô */}
      {boss && characterNFT && (
        <div id="toast" className={showToast ? "show" : ""}>
          <div id="desc">{`üí• ${boss.name} was hit for ${characterNFT.attackDamage}!`}</div>
        </div>
      )}
      {boss && (
        <div className="boss-container">
          {/* attackState ËøΩÂä†„Åó„Åæ„Åô */}
          <div className={`boss-content  ${attackState}`}>
            <h2>üî• {boss.name} üî•</h2>
            <div className="image-content">
              <img src={boss.imageURI} alt={`Boss ${boss.name}`} />
              <div className="health-bar">
                <progress value={boss.hp} max={boss.maxHp} />
                <p>{`${boss.hp} / ${boss.maxHp} HP`}</p>
              </div>
            </div>
          </div>
          <div className="attack-container">
            <button className="cta-button" onClick={runAttackAction}>
              {`üí• Attack ${boss.name}`}
            </button>
          </div>
          {/* Attack „Éú„Çø„É≥„ÅÆ‰∏ã„Å´„É≠„Éº„Éá„Ç£„É≥„Ç∞„Éû„Éº„ÇØ„ÇíËøΩÂä†„Åó„Åæ„Åô*/}
          {attackState === "attacking" && (
            <div className="loading-indicator">
              <LoadingIndicator />
              <p>Attacking ‚öîÔ∏è</p>
            </div>
          )}
        </div>
      )}
      {/* NFT „Ç≠„É£„É©„ÇØ„Çø„Éº */}
      {characterNFT && (
        <div className="players-container">
          <div className="player-container">
            <h2>Your Character</h2>
            <div className="player">
              <div className="image-content">
                <h2>{characterNFT.name}</h2>
                <img
                  src={characterNFT.imageURI}
                  alt={`Character ${characterNFT.name}`}
                />
                <div className="health-bar">
                  <progress value={characterNFT.hp} max={characterNFT.maxHp} />
                  <p>{`${characterNFT.hp} / ${characterNFT.maxHp} HP`}</p>
                </div>
              </div>
              <div className="stats">
                <h4>{`‚öîÔ∏è Attack Damage: ${characterNFT.attackDamage}`}</h4>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};
export default Arena;
